import React, { useContext, useState } from "react";
import { View, Text, TouchableOpacity, TextInput, ImageBackground, StyleSheet, ScrollView, Image } from "react-native";
import { Ionicons, AntDesign, FontAwesome, Feather, Entypo, MaterialIcons } from '@expo/vector-icons';

import AppLoading from "expo-app-loading";

import {
    useFonts,
    Poppins_100Thin,
    Poppins_100Thin_Italic,
    Poppins_200ExtraLight,
    Poppins_200ExtraLight_Italic,
    Poppins_300Light,
    Poppins_300Light_Italic,
    Poppins_400Regular,
    Poppins_400Regular_Italic,
    Poppins_500Medium,
    Poppins_500Medium_Italic,
    Poppins_600SemiBold,
    Poppins_600SemiBold_Italic,
    Poppins_700Bold,
    Poppins_700Bold_Italic,
    Poppins_800ExtraBold,
    Poppins_800ExtraBold_Italic,
    Poppins_900Black,
    Poppins_900Black_Italic,
} from '@expo-google-fonts/poppins';
import colors from "../constant/colors";
import YellowBtn from "../components/button/YellowBtn";
import AddWorkspaceInput from "../components/TextInput/AddWorkspaceInput";
import Header from "../components/header/Header";
import DoneBtn from "../components/button/DoneBtn";
import Inputfield from "../components/TextInput/TextInput";
import { useNavigation } from "@react-navigation/native";
import { accessCamera, accessGallery } from "../utils/ImagePicker";
import * as ImagePicker from 'expo-image-picker';
import WorkspaceCard from "../components/card/WorkspaceCard";
import { AuthContext } from "../Context/Auth";
const ViewWorkSpace = (props) => {



    let [fontsLoaded] = useFonts({
        Poppins_100Thin,
        Poppins_100Thin_Italic,
        Poppins_200ExtraLight,
        Poppins_200ExtraLight_Italic,
        Poppins_300Light,
        Poppins_300Light_Italic,
        Poppins_400Regular,
        Poppins_400Regular_Italic,
        Poppins_500Medium,
        Poppins_500Medium_Italic,
        Poppins_600SemiBold,
        Poppins_600SemiBold_Italic,
        Poppins_700Bold,
        Poppins_700Bold_Italic,
        Poppins_800ExtraBold,
        Poppins_800ExtraBold_Italic,
        Poppins_900Black,
        Poppins_900Black_Italic,
    });
    const [visible, setVisible] = useState(false);
    const navigation = useNavigation()


    const {setTaskId , currentWorkspace} = useContext(AuthContext)
    const openGallery = async () => {
        // No permissions request is necessary for launching the image library
        let result = await ImagePicker.launchImageLibraryAsync({
            mediaTypes: ImagePicker.MediaTypeOptions.All,
            quality: 1,
            allowsMultipleSelection: true,
            base64: false,
        }).then((e) => {
            if (!e.cancelled) {
                multipleimages.length === 0 ?
                    setMultipleimages([e])
                    : setMultipleimages(previuos => {
                        return [...previuos, e];
                    });
                setVisible(false);
                // setImage(result.uri);
            }
        }).catch((err) => {
            console.log(err)
        });
    };


    const images = [
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
        'https://images.g2crowd.com/uploads/attachment/file/146244/Board-View.png',
    ]

    const workspaceCard = [
        {
            image: 'https://media.istockphoto.com/photos/business-people-joining-gears-picture-id1254347556?k=20&m=1254347556&s=612x612&w=0&h=6PCSBI7MAfsu9CoI4xqsj_2oIenEUR1HYb8X5mRwttU=',
            avatar: 'https://w7.pngwing.com/pngs/340/946/png-transparent-avatar-user-computer-icons-software-developer-avatar-child-face-heroes.png',
            title: 'React native Developer',
            dueDate: '12-Aug-2022',
            taskAsign: 'Muhammad Ayan',
            discription: 'Lorem ipsum is placeholder text commonly used in the graphic, print, and publishing industries for previewing layouts and visual mockups'
        },
        {
            image: 'https://resume-example.com/wp-content/uploads/2021/03/teamwork-concept-on-the-brown-wooden-table-background.jpg',
            avatar: 'https://toppng.com/uploads/preview/cool-avatar-transparent-image-cool-boy-avatar-11562893383qsirclznyw.png',
            title: 'Full Stack Developer',
            dueDate: '13-July-2022',
            taskAsign: 'Muhammad Bilal',
            discription: 'Lorem ipsum is placeholder text commonly used in the graphic, print, and publishing industries for previewing layouts and visual mockups'
        },
        {
            image: 'https://elearningindustry.com/wp-content/uploads/2019/12/make-teamwork-effective-in-project-management.jpg',
            avatar: 'https://spng.pngfind.com/pngs/s/5-53481_med-boukrima-specialist-webmaster-php-e-commerce-web.png',
            title: 'backend Developer',
            dueDate: '20-july-2022',
            taskAsign: 'Muhammad Shahbaz',
            discription: 'Lorem ipsum is placeholder text commonly used in the graphic, print, and publishing industries for previewing layouts and visual mockups'
        },
    ]


    return !fontsLoaded ? <AppLoading /> : (

        <ScrollView style={styles.Mainview}>
            <Header PressIconPlus={() => {
                navigation.navigate('createtask')
                setTaskId(currentWorkspace.tasks.length + 2000)
            }}
                admin={true} endHeading={'View WorkSpace'}
                headingStyle={{ fontSize: 20, color: 'black', fontFamily: 'Poppins_700Bold' }} />


            <Text style={[styles.activity, { fontSize: 20 }]} >Workspace Task Board</Text>

            {workspaceCard.map((item, index) => {
                return (
                    <WorkspaceCard
                        title={item.title}
                        discription={item.discription}
                        duedate={item.dueDate}
                        taskasign={item.taskAsign}
                        image={item.image}
                        avatar={item.avatar}
                        onPress={() => navigation.navigate('ViewTaskDetail')}
                    />
                )
            })}

        </ScrollView>
    )

}

export default ViewWorkSpace;

const styles = StyleSheet.create({
    Mainview: { flex: 1, backgroundColor: '#fff' },
    DoneBtn: { backgroundColor: colors.blue, marginHorizontal: 37, padding: 10, borderRadius: 100, height: 80, width: 80, alignItems: 'center', justifyContent: 'center' },
    DoneBtntext: { color: 'white', fontFamily: 'Poppins_500Medium', textAlign: 'center' },
    activity: { fontSize: 14, color: 'black', fontFamily: 'Poppins_700Bold', marginHorizontal: 15, marginVertical: 30 },
    input: {
        fontSize: 12, fontFamily: 'Poppins_500Medium', color: 'black', marginTop: 5, marginHorizontal: 18, paddingVertical: 0, padding: 0,
        flexDirection: 'row',
        backgroundColor: 'white',
        marginHorizontal: 15,
        paddingVertical: 10,
        borderRadius: 5,
        paddingHorizontal: 10,
        // marginTop: 40,
        //shadow
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
    LeaveBtn: { justifyContent: 'center', alignItems: 'center' },


})
