import React, { useContext, useEffect, useState } from "react";
import { View, Text, TouchableOpacity, TextInput, StyleSheet, ScrollView, Image, ImageBackground } from "react-native";
import AppLoading from "expo-app-loading";

import {
    useFonts,
    Poppins_100Thin,
    Poppins_100Thin_Italic,
    Poppins_200ExtraLight,
    Poppins_200ExtraLight_Italic,
    Poppins_300Light,
    Poppins_300Light_Italic,
    Poppins_400Regular,
    Poppins_400Regular_Italic,
    Poppins_500Medium,
    Poppins_500Medium_Italic,
    Poppins_600SemiBold,
    Poppins_600SemiBold_Italic,
    Poppins_700Bold,
    Poppins_700Bold_Italic,
    Poppins_800ExtraBold,
    Poppins_800ExtraBold_Italic,
    Poppins_900Black,
    Poppins_900Black_Italic,
} from '@expo-google-fonts/poppins';
import colors from "../constant/colors";

// icons
import { Ionicons, Entypo, Octicons, AntDesign } from '@expo/vector-icons';
import Inputfield from "../components/TextInput/TextInput";
import Card from "../components/card/Card";
import { useNavigation } from "@react-navigation/native";
import { AuthContext } from './../Context/Auth'
import axios from "axios"

const data = [
    {
        image: 'https://static1.makeuseofimages.com/wordpress/wp-content/uploads/2016/10/camera-photo-lens-stock-images.jpg?q=50&fit=contain&w=1500&h=750&dpr=1.5',
        count: 1,
        users: '+2',
        teamname: 'android',
        duedate: 'Due Date',
        text: 'Details will be mentioned here, user will be able to see the additional information upon click...',
    },
    {
        image: 'https://images.unsplash.com/photo-1535890696255-dd5bcd79e6df?ixlib=rb-1.2.1&ixid=MnwxMjA3fDB8MHxleHBsb3JlLWZlZWR8MXx8fGVufDB8fHx8&w=1000&q=80',
        count: 4,
        users: '+7',
        teamname: 'Group',
        duedate: 'Due Date',
        text: 'Details will be mentioned here, user will be able to see the additional information upon click...',
    },

]

const Images = [
    {
        image: 'https://www.newsshare.in/wp-content/uploads/2017/04/Miniclip-8-Ball-Pool-Avatar-16.png',
        name: 'peter'
    },
    {
        image: 'https://www.newsshare.in/wp-content/uploads/2017/04/Miniclip-8-Ball-Pool-Avatar-16.png',
        name: 'Lisa'
    },
    {
        image: 'https://www.newsshare.in/wp-content/uploads/2017/04/Miniclip-8-Ball-Pool-Avatar-16.png',
        name: 'Murr'
    },
    {
        image: 'https://www.newsshare.in/wp-content/uploads/2017/04/Miniclip-8-Ball-Pool-Avatar-16.png',
        name: 'Billy'
    },
]

const WorkSpaceImage = [
    {
        image: 'https://www.incimages.com/uploaded_files/image/1920x1080/getty_517610514_353435.jpg',
        name: 'My Team'
    },
    {
        image: 'https://www.incimages.com/uploaded_files/image/1920x1080/getty_517610514_353435.jpg',
        name: 'Personal'
    },
    {
        image: 'https://www.incimages.com/uploaded_files/image/1920x1080/getty_517610514_353435.jpg',
        name: 'Development'
    },
    {
        image: 'https://www.incimages.com/uploaded_files/image/1920x1080/getty_517610514_353435.jpg',
        name: 'Q/A'
    },
]

const FullList = (props) => {
    const navigation = useNavigation()
    const { user, setCurrentWorkspace } = useContext(AuthContext)
    const [myWorkSpace, setMyWorkSpace] = useState()

    const getWorkSpace = () => {
        const form = {
            userId: user?._id
        }
        axios.post('http://192.168.0.106:5000/workspace/myWorkspace/get', form)
            .then(function (response) {
                setMyWorkSpace(response.data)
            })
            .catch(function (error) {
                console.log(error);
            });
    }


    useEffect(() => {
        getWorkSpace()
    }, [])



    let [fontsLoaded] = useFonts({
        Poppins_100Thin,
        Poppins_100Thin_Italic,
        Poppins_200ExtraLight,
        Poppins_200ExtraLight_Italic,
        Poppins_300Light,
        Poppins_300Light_Italic,
        Poppins_400Regular,
        Poppins_400Regular_Italic,
        Poppins_500Medium,
        Poppins_500Medium_Italic,
        Poppins_600SemiBold,
        Poppins_600SemiBold_Italic,
        Poppins_700Bold,
        Poppins_700Bold_Italic,
        Poppins_800ExtraBold,
        Poppins_800ExtraBold_Italic,
        Poppins_900Black,
        Poppins_900Black_Italic,
    });
    const [value, setValue] = useState('')

    return !fontsLoaded ? <AppLoading /> : (
        <ScrollView>

            <View style={{ paddingBottom: 5 }}>
                <View style={styles.MainView}>
                    <View style={styles.Drawer}>
                        <TouchableOpacity onPress={() => navigation.openDrawer()}>
                            <Ionicons name="reorder-three" size={40} color="black" />
                        </TouchableOpacity>
                        <View style={styles.iconView}>

                            <Text style={styles.trello}>trello</Text>
                            <Octicons name="dot" size={20} style={styles.icon} color={colors.yellow} />
                        </View>
                    </View>
                    <TouchableOpacity onPress={() => navigation.navigate('search')}>

                        <AntDesign
                            name="search1"
                            size={24}
                            color="black"
                            style={{ paddingHorizontal: 20 }}
                        />
                    </TouchableOpacity>

                </View>
                <View style={{}}>
                    <Inputfield
                        placeholderTextColor={colors.grey}
                        placeholder="Enter a name of a team you want to be added in"
                        onChange={(e) => setValue(e)}
                        value={value}
                        textStyle={styles.input}
                    />
                </View>
            </View>


            <View>
                <Text style={{ fontSize: 20, color: 'black', marginTop: 22, fontFamily: 'Poppins_800ExtraBold', marginHorizontal: 15, marginBottom: 20 }}>Recent Workspaces</Text>

                <ScrollView horizontal={true}>
                    <View style={{ flexDirection: 'row', alignItems: 'center', justifyContent: 'space-around', marginHorizontal: 15 }}>

                        <View style={{ flexDirection: 'row', justifyContent: 'space-around' }}>
                            {WorkSpaceImage.map((item, index) => {
                                return (
                                    <ImageBackground source={{ uri: item.image }} style={{ height: 200, width: 155, marginRight: 10, flex: 1, justifyContent: 'space-between' }}>
                                        <Text>.</Text>
                                        <Text style={{ color: 'white', backgroundColor: 'black', fontFamily: 'Poppins_700Bold', marginRight: 20, textAlign: 'center', paddingVertical: 10 }}>{item.name}</Text>

                                    </ImageBackground>
                                )
                            })}
                        </View>
                    </View>
                </ScrollView>

            </View>


            <View>
                <Text style={{ fontSize: 20, color: 'black', marginTop: 22, fontFamily: 'Poppins_800ExtraBold', marginHorizontal: 15, marginBottom: 20 }}>All Workspaces</Text>

                {myWorkSpace ? myWorkSpace.length === 0 ?
                    <View><Text>No Workspace Found</Text></View> :
                    myWorkSpace?.map((item, index) => {
                        return (
                            <TouchableOpacity key={index} activeOpacity={0.9} onPress={() => {
                                navigation.navigate("viewworkspace")
                                setCurrentWorkspace(item)
                            }}>
                                <Card
                                    image={item?.workSpaceImage ? item.workSpaceImage : "https://www.incimages.com/uploaded_files/image/1920x1080/getty_517610514_353435.jpg"}
                                    count="1"
                                    users={item?.users.length}
                                    teamname={item?.workSpaceName}
                                    duedate="15/7/2022"
                                    text={item?.about}
                                    title={'Leave'}
                                    chatting={() => navigation.navigate('chatting')}
                                />
                            </TouchableOpacity>
                        )
                    }) : null}
            </View>

            {/* <View>
                <Text style={{ fontSize: 20, color: 'black', marginTop: 22, fontFamily: 'Poppins_800ExtraBold', marginHorizontal: 15, marginBottom: 20 }}>Recently Viewed</Text>


                {data.map((item, index) => {
                    return (
                        <TouchableOpacity activeOpacity={0.9} onPress={() => navigation.navigate('postview')}>

                            <Card
                                image={item.image}
                                count={item.count}
                                users={item.users}
                                teamname={item.teamname}
                                duedate={item.duedate}
                                text={item.text}
                                title={'Leave'}
                            />
                        </TouchableOpacity>
                    )
                })}
            </View> */}

        </ScrollView>


    )
}

export default FullList;

const styles = StyleSheet.create({
    MainView: { flexDirection: 'row', paddingTop: 40, paddingBottom: 0, justifyContent: 'space-between', alignItems: 'center' },
    Drawer: { flexDirection: 'row', alignItems: 'center' },
    trello: { fontSize: 29, color: colors.yellow, fontFamily: 'Poppins_700Bold', },
    Searchbar: { fontSize: 12, color: 'black', marginRight: 18, fontFamily: 'Poppins_700Bold', color: colors.grey },
    input: {
        fontSize: 12, fontFamily: 'Poppins_500Medium', color: colors.grey, marginTop: 5, marginHorizontal: 18, paddingVertical: 0, padding: 0,
        flexDirection: 'row',
        backgroundColor: 'white',
        marginHorizontal: 15,
        paddingVertical: 10,
        paddingHorizontal: 10,
        // marginTop: 40,
        //shadow
        shadowColor: '#000',
        shadowOffset: {
            width: 0,
            height: 2,
        },
        shadowOpacity: 0.25,
        shadowRadius: 3.84,
        elevation: 5,
    },
    iconView: { flexDirection: 'row', width: 200 },
    icon: { paddingHorizontal: 5, marginTop: 20 }
});
